sys_variable_value.value:
(function execute(inputs, outputs) {
  //check if the api call worked
  if(inputs.status_code = '200'){
    //parse response to json and save as object
    var responseBody = JSON.parse(inputs.response_body);
    //Map Data from responseBody to outputs
    outputs.hostname = responseBody.hostname;
    outputs.city = responseBody.city;
    outputs.region = responseBody.region;
    outputs.country = responseBody.country;

    //split returned location into latitude and longitude
    var locArray = responseBody.loc.split(",");
    outputs.latitude = locArray[0];
    outputs.longitude = locArray[1];
  }
})(inputs, outputs);

